function HTMLAddListener(id,fn,type,capture)
{var myElement=document.getElementById(id);myElement.addEventListener(type,fn,capture);}
function HTMLRemoveListener(id,fn,type,capture)
{var myElement=document.getElementById(id);myElement.removeEventListener(type,fn,capture);}
