{dumpSomeEventPropertiesHTML(evt,"menu_on1 ");alert('HTMLmenu_on1');}
function HTMLmenu_on2(evt)
{dumpSomeEventPropertiesHTML(evt,"menu_on2 ");}
function HTMLmenu_on3(evt)
{dumpSomeEventPropertiesHTML(evt,"menu_on3 ");}
function dumpSomeEventPropertiesHTML(evt,name)
{var msg=name+getTimeStamp()+"\n";msg+='evt.which '+evt.which+"\n";msg+='evt.keyCode '+evt.keyCode+"\n";msg+='evt.charCode '+evt.charCode+"\n";msg+='evt.pageX  '+evt.pageX+"\n";msg+='evt.pageY '+evt.pageY+"\n";msg+='evt.isChar '+evt.isChar+"\n";msg+='evt.screenX '+evt.screenX+"\n";msg+='evt.screenY '+evt.screenY+"\n";msg+='evt.clientX '+evt.clientX+"\n";msg+='evt.clientY '+evt.clientY+"\n";msg+='evt.isTrusted '+evt.isTrusted+"\n";msg+='evt.eventPhase '+evt.eventPhase+"\n";var phase;switch(evt.eventPhase)
{case 1:phase=" CAPTURING_PHASE ";break;case 2:phase=" AT_TARGET ";break;case 3:phase=" BUBBLING_PHASE ";break;default:phase=" not 123 ";break;}
msg+='evt.eventPhase '+evt.eventPhase+phase+"\n";msg+='evt.bubbles '+evt.bubbles+"\n";msg+='evt.cancelable '+evt.cancelable+"\n";msg+='evt.type '+evt.type+"\n";msg+='evt.target.id '+evt.target.id+"\n";msg+='evt.currentTarget.id '+evt.currentTarget.id+"\n";alert(msg);}
